<script>/*- Hàm validate form*/function validateOrderForm() {	var vname = document.getElementById('name');	var vemail = document.getElementById('email');	var vsubject = document.getElementById('subject');	var vdetail = document.getElementById('detail');		var name_error = document.getElementById('name-contact-error');	var email_error = document.getElementById('email-contact-error');	var subject_error = document.getElementById('subject-contact-error');	var detail_error = document.getElementById('detail-contact-error');			if(name_Validation(vname) == true && email_Validation(vemail) == true && 	subject_Validation(vsubject) == true && detail_Validation(vdetail) == true) {		return true;	}else {		if(name_Validation(vname) == "required") {			name_error.innerHTML = "Nhập họ tên vào đây";		}else if(name_Validation(vname) == "minlength") {			name_error.innerHTML = "Tối thiểu 4 ký tự";		}else if(name_Validation(vname) == "maxlength") {			name_error.innerHTML = "Tối đa 100 ký tự";		}else {					}				if(email_Validation(vemail) == "required") {			email_error.innerHTML = "Hãy nhập địa chỉ email vào đây";		}else if(email_Validation(vemail) == "notvalid") {			email_error.innerHTML = "Địa chỉ email không hợp lệ";		}else {				}				if(subject_Validation(vsubject) == "required") {			subject_error.innerHTML = "Mời bạn nhập tiêu đề";		}else if(subject_Validation(vsubject) == "minlength" ) {			subject_error.innerHTML = "Tối thiểu 4 ký tự";		}else if(subject_Validation(vsubject) == "maxlength") {			subject_error.innerHTML = "Tối đa 200 ký tự";		}else {				}				if(detail_Validation(vdetail) == "required") {			detail_error.innerHTML = "Mời bạn nhập nội dung";		}else if(detail_Validation(vdetail) == "minlength") {			detail_error.innerHTML = "Tối thiểu 35 ký tự";		}else {				}			}	return false;}// Validate namefunction name_Validation(name) {	var name_length = name.value.length;	var name_string = name.value;	if(name_string == '') {		return "required";	}else if(name_length < 4) {		return "minlength";	}else if(name_length > 100) {		return "maxlength";	}else {		return true;	}}// Validate emailfunction email_Validation(email) {	var valid_email = /^([\w-]+(?:\.[\w-]+)*)@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$/i;	var email_string = email.value;	if(email_string == '') {		return "required";	}else if(!email_string.test(email_string)) {		return "notvalid";	}else {		return true;	}}// Validate subjectfunction subject_Validation(subject) {	var subject_length = subject.value.length;	var subject_string = subject.value;	if(subject_string == '') {		return "required";	}else if(subject_length < 4) {		return "minlength";	}else if(subject_length > 200) {		return "maxlength";	}else {		return true;	}}// Validate detailfunction detail_Validation(detail) {	var detail_string = detail.value;	var detail_length = detail.value.length;	if(detail_string == '') {		return "required";	}else if(detail_length < 35) {		return "minlength";	}else {		return true;	}}</script>		<!---------------- MAIN CONTENT ---------------->		<div id="mainContent">				<div id="order-form">				<form action="" method="post" onsubmit="return validateOrderForm()">				<fieldset>				<legend>Thông tin đơn hàng</legend>				 				 				<label for='name' >Tên đầy đủ*: </label><br/>				<input type='text' name='name' id='name'  maxlength="50" /><span id="name-contact-error"></span><br/>			 				<label for='email' >Email*:</label><br/>				<input type='text' name='email' id='email' maxlength="50" /><span id="email-contact-error"></span><br/>			 				<label for='subject' >Tiêu đề*:</label><br/>				<input type='text' name='subject' id='subject' maxlength="50" /><span id="subject-contact-error"></span><br/>			 				<label for='detail' >Chi tiết*:</label><br/>				<textarea name='detail' id='detail'> </textarea>				<script type="text/javascript">CKEDITOR.replace('detail'); </script>				<span id="detail-contact-error"></span>				<br />				<input type='submit' name='Submit' value='Gửi' id="submit" />			 				</fieldset>				</form>			</div>			<div class="clearfix"></div>		</div><!-- End #mainContent -->