		<script language="javascript">			function del(pid){				if(confirm('Bạn có chắc chắn xóa sản phẩm này không?')){					document.formcart.pid.value = pid;				}			}		</script>		<div id="mainContent">					<div id="show-cart">					<p style="padding: 5px 0px 0px 0px; font-family: Vernada, Tahoma, Georgia;"><?php if(isset($msg)) echo $msg; ?></p>					<form name="formcart" action="" method="post">					<input type="hidden" name="pid" />					<table>						<thead>							<tr class="order-cart-thead" >								<th class="order-cart-stt" >STT</th>								<th class="order-cart-name" >Tên sách</th>								<th class="order-cart-price" >Đơn giá(VNĐ)</th>								<th class="order-cart-quantity" >Số lượng</th>								<th class="order-cart-option" >Tùy chọn</th>								<th class="order-cart-amount" >Thành tiền(VNĐ)</th>							</tr>						</thead>						<tbody>							<?php 								//Lấy dữ liệu sản phẩm từ session								if(isset($_SESSION['cart']) && count($_SESSION['cart']) > 0)								{									$max = count($_SESSION['cart']);									$total = 0;									for($i = 0; $i < $max; $i++)									{										$pid = $_SESSION['cart'][$i]['product_id'];										$q = $_SESSION['cart'][$i]['quantity'];										$product = product_get_by_id($pid);										$total += $product['price']*$q;							?>															<tr class="order-cart-tbody" >								<td class="order-cart-stt" ><?php echo $i + 1; ?></td>								<td class="order-cart-name" ><a href="<?php echo SITE_URL.'product/showproduct.php?product_id='.$pid; ?>"><?php echo $product['name']; ?></a></td>								<td class="order-cart-price" ><?php echo number_format($product['price']); ?></td>								<td class="order-cart-quantity" ><input type="text" name="product<?php echo $pid; ?>" value="<?php echo $q; ?>" class="order-cart-input" /></td>								<td class="order-cart-option" ><input type="submit" name="remove" value="Xóa" onclick="return del(<?php echo $pid; ?>)" /></td>								<td class="order-cart-amount" ><?php echo number_format($product['price']*$q); ?></td>							</tr>														<?php									}								echo "</tbody></table>";								}								else								{									echo "</tbody></table>";									echo "<p>Không có sản phẩm nào trong giỏ hàng của bạn</p>";								}							?>					<ul>						<li class="amount-cart">Tổng giá tiền<span><?php if(isset($total)) echo number_format($total); else echo 0; ?></span></li>						<li class="clear-cart-button"><input type="submit" name="clear" value="Xóa giỏ" onclick="return confirm('Bạn có chắc chắn xóa giỏ hàng không?')"/></li>						<li class="update-cart-button"><input type="submit" name="update" value="Cập nhật" /></li>					</ul>					</form>					<p>					<input class="payment" type="button" name="payment" value="Thanh toán" onclick="window.location='payment.php'" />					<input class="continue-shopping" type="button" value="Tiếp tục mua hàng" onclick="window.location='..'" />					</p>				</div><!-- End #show-cart -->		</div><!-- End #mainContent -->